mapper.select:
SELECT t.F_PRODUCTID as productId , t.F_SPID as spId , t.F_VENID as venId , t.F_VENNAME as venName , t.F_PRODUCTNAME as productName , t.F_PRODUCTINFO as productInfo , t.F_FEEMEMO as feeMemo , t.F_TIGINFO as tigInfo , t.F_STATE as state , t.F_THUMBPIC as thumbPic , t.F_REORDER as reorder , t.F_SUBJECT as subject , t.F_SALENUM as saleNum , t.F_CREATORUID as creatorUid , t.F_CREATOR as creator , t.F_CREATETIME as createrTime , t.F_UPDATETIME as updateTime , t.F_UPDATERUID as updaterUid , t.F_UPDATER as updater , t.F_DELFLAG as delFlag , t.F_RECOMMEND as recommend , t.F_TYPE as type , t.F_SUBTYPE as subType, t.F_PRODUCTCODE as productCode, p.F_TYPENAME as typeName, t.F_SORTNUM as sortNum, t.F_REFERENCEPRICE as referencePrice FROM t_sm_commproduct t LEFT JOIN t_sm_pdctype p on t.F_TYPE = p.F_PDCTYPEID
and t.F_PRODUCTID = #{productId}
and t.F_DELFLAG = #{delFlag}
and t.F_SPID = #{spId}
and t.F_PRODUCTNAME like concat('%',#{productName},'%')
and t.F_PRODUCTCODE like concat('%',#{productCode},'%')
and t.F_TYPE = #{type}
and t.F_VENID = #{venId}
and t.F_STATE = #{state}
and t.F_SORTNUM is null
and t.F_SORTNUM is not null
ORDER BY ${entityPage.sortField}
${entityPage.sortOrder}

SELECT t.F_PRODUCTID as productId , t.F_SPID as spId , t.F_VENID as venId , t.F_VENNAME as venName , t.F_PRODUCTNAME as productName , t.F_PRODUCTINFO as productInfo , t.F_FEEMEMO as feeMemo , t.F_TIGINFO as tigInfo , t.F_STATE as state , t.F_THUMBPIC as thumbPic , t.F_REORDER as reorder , t.F_SUBJECT as subject , t.F_SALENUM as saleNum , t.F_CREATORUID as creatorUid , t.F_CREATOR as creator , t.F_CREATETIME as createrTime , t.F_UPDATETIME as updateTime , t.F_UPDATERUID as updaterUid , t.F_UPDATER as updater , t.F_DELFLAG as delFlag , t.F_RECOMMEND as recommend , t.F_TYPE as type , t.F_SUBTYPE as subType, t.F_PRODUCTCODE as productCode, p.F_TYPENAME as typeName, tem.F_PRICEID as priceId, t.F_REFERENCEPRICE as referencePrice FROM t_sm_commproduct t LEFT JOIN t_sm_pdctype p on t.F_TYPE = p.F_PDCTYPEID LEFT JOIN (select F_PRODUCTID,F_PRICEID FROM t_sm_sellprice s where s.F_PRICETYPE=0 group By s.F_PRODUCTID) tem on tem.F_PRODUCTID = t.F_PRODUCTID
and t.F_PRODUCTID = #{productId}
and t.F_DELFLAG = #{delFlag}
and t.F_SPID = #{spId}
and t.F_PRODUCTNAME like concat('%',#{productName},'%')
and t.F_PRODUCTCODE like concat('%',#{productCode},'%')
and t.F_TYPE = #{type}
and t.F_VENID = #{venId}
and t.F_STATE = #{state}
and t.F_SORTNUM is not null
and t.F_SORTNUM is null
ORDER BY ${entityPage.sortField}
${entityPage.sortOrder}
