mapper.delete:
DELETE FROM
	t_sm_couponcommproduct 


		F_COUPONCOMMID = #{couponCommId}


		and F_COUPONID = #{couponId}


		and F_SOURCETYPE = #{sourceType}

mapper.select:
SELECT t.F_COUPONCOMMID as couponCommId , t.F_COUPONID as couponId , t.F_USECOUPONPDCID as useCouponPdcId , t.F_ISCOMMON as isCommon , t.F_ISGROUPPRO as isGroupPro , t.F_ISSECKILL as isSeckill , t.F_CREATETIME as createTime, t.F_SOURCETYPE as sourceType FROM t_sm_couponcommproduct t
t.F_COUPONCOMMID = #{couponCommId}
and t.F_COUPONID = #{couponId}
and t.F_USECOUPONPDCID = #{useCouponPdcId}

SELECT t.F_COUPONCOMMID as couponCommId , t.F_COUPONID as couponId , t.F_USECOUPONPDCID as useCouponPdcId , t.F_ISCOMMON as isCommon , t.F_ISGROUPPRO as isGroupPro , t.F_ISSECKILL as isSeckill , t.F_CREATETIME as createTime, t.F_SOURCETYPE as sourceType FROM t_sm_couponcommproduct t
t.F_COUPONCOMMID = #{couponCommId}
and t.F_COUPONID = #{couponId}
ORDER BY ${entityPage.sortField}
${entityPage.sortOrder}
