mapper.delete:
DELETE FROM
	t_sm_couponproduction 


		F_COUPONPDCID = #{couponPdcId}


    	AND F_COUPONID = #{couponId}

mapper.select:
SELECT
	t.F_COUPONPDCID as couponPdcId ,
	t.F_COUPONID as couponId ,
	t.F_USECOUPONID as useCouponId ,
	t.F_COUPONTYPE as couponType 
FROM
t_sm_couponproduction t


    	t.F_COUPONPDCID = #{couponPdcId}


    	t.F_COUPONID = #{couponId}




    	ORDER BY ${entityPage.sortField}




    	${entityPage.sortOrder}

SELECT
	t.F_USECOUPONID as useCouponId
FROM
t_sm_couponproduction t


    	t.F_COUPONPDCID = #{couponPdcId}


    	t.F_COUPONID = #{couponId}
