mapper.select:
SELECT t.F_CUSTOMERCOUPONID as customerCouponId , t.F_CUSTOMERCODE as customerCode , t.F_COUPONID as couponId , t.F_USETYPE as useType , t.F_USELIMIT as useLimit , t.F_DISCOUNT as discount , t.F_COUPONTYPE as couponType , t.F_VALIDBEGIN as validBegin , t.F_VALIDEND as validEnd , t.F_OBTAINTIME as obtainTime , t.F_STATUS as status , t.F_ORDERID as orderId , t.F_DELFLAG as delFlag , t.F_PCOUPONID as pcouponId FROM t_sm_customercoupon t
t.F_CUSTOMERCOUPONID = #{customerCouponId}
ORDER BY ${entityPage.sortField}
${entityPage.sortOrder}
