mapper.delete:
DELETE FROM
	t_sm_expressprice 


    	F_EXPRESSPRICEID = #{expressPriceId}


    	and F_COMPANYEXPRESSID = #{companyExpressId}

mapper.select:
SELECT t.F_EXPRESSPRICEID as expressPriceId , t.F_COMPANYEXPRESSID as companyExpressId , t.F_PROVINCE as province , t.F_EXPRESSNUM as expressNum , t.F_EXPRESSPRICE as expressPrice , t.F_EXPRESSNUMADD as expressNumAdd , t.F_EXPRESSPRICEADD as expressPriceAdd , t.F_GROUPNUM as groupNum, t.F_PROVINCEID as provinceId FROM t_sm_expressprice t
t.F_EXPRESSPRICEID = #{expressPriceId}
ORDER BY ${entityPage.sortField}
${entityPage.sortOrder}

SELECT t.F_COMPANYEXPRESSID AS companyExpressId, group_concat(t.F_PROVINCE) AS province, t.F_EXPRESSNUM AS expressNum, t.F_EXPRESSPRICE AS expressPrice, t.F_EXPRESSNUMADD AS expressNumAdd, t.F_EXPRESSPRICEADD AS expressPriceAdd, t.F_GROUPNUM AS groupNum, group_concat(t.F_PROVINCEID) AS provinceId FROM t_sm_expressprice t
t.F_EXPRESSPRICEID = #{expressPriceId}
t.F_COMPANYEXPRESSID = #{companyExpressId}
ORDER BY ${entityPage.sortField}
${entityPage.sortOrder}
group by t.F_GROUPNUM
