mapper.delete:
UPDATE t_sm_goodsprice SET F_DELFLAG = '1'
F_GOODSPRICEID in
#{param}
and F_PRODUCTID = #{productId}

mapper.select:
SELECT t.F_GOODSPRICEID as goodsPriceId , t.F_PRODUCTID as productId , t.F_PRICENAME as priceName , t.F_STORECOUNT as storeCount , t.F_THUMBPIC as thumbPic , t.F_CREATETIME as createTime , s.F_PRICE as price, s.F_LIMITBUYCOUNT as limitBuyCount, s.F_MAXBUYCOUNT as maxBuyCount, s.F_PRICESTARTDATE as priceStartDate, s.F_PRICEENDDATE as priceEndDate, s.F_PEOPLENUM as peopleNum, t.F_DELFLAG as delFlag, s.F_PRICEID as priceId, t.F_FREIGHT as freight FROM t_sm_goodsprice t left join t_sm_sellprice s on t.F_GOODSPRICEID = s.F_GOODSPRICEID
and s.F_PRICETYPE = #{priceType}
t.F_GOODSPRICEID = #{goodsPriceId}
and t.F_GOODSPRICEID in #{param}
and t.F_PRODUCTID = #{productId}
and t.F_DELFLAG = #{delFlag}
and s.F_PRICEID = #{priceId}
ORDER BY ${entityPage.sortField}
${entityPage.sortOrder}

SELECT t.F_PRICEID as priceId , t.F_GOODSPRICEID as goodsPriceId , t.F_PRODUCTID as productId , t.F_PRICE as price , t.F_PRICETYPE as priceType , t.F_PRICESTARTDATE as priceStartDate , t.F_PRICEENDDATE as priceEndDate , t.F_MAXBUYCOUNT as maxBuyCount , t.F_LIMITBUYCOUNT as limitBuyCount , t.F_PEOPLENUM as peopleNum , t.F_DELFLAG as delFlag, t.F_UPDATETIME as updateTime, g.F_PRICENAME as priceName , g.F_STORECOUNT as storeCount , g.F_THUMBPIC as thumbPic , g.F_FREIGHT as freight FROM t_sm_sellprice t LEFT JOIN t_sm_goodsprice g on t.F_GOODSPRICEID = g.F_GOODSPRICEID
and t.F_PRICEID = #{priceId}
and t.F_GOODSPRICEID = #{goodsPriceId}
and t.F_PRICETYPE = #{priceType}
