mapper.update:
UPDATE t_sm_orderlist
F_ORDERID = #{orderId} , F_SPID = #{spId} , F_VENID = #{venId} , F_VENNAME = #{venName} , F_PRICEID = #{priceId} , F_PRODUCTID = #{productId} , F_PRODUCTNAME = #{productName} , F_PRICE = #{price} , F_NUM = #{num} , F_COUPON_MONEY = #{couponMoney} , F_CUSTOMERCOUPONID = #{customerCouponId} , F_MONEY = #{money} , F_PAYMONEY = #{payMoney} , F_BACKNUM = #{backNum} , F_GOODSPRICEID = #{goodsPriceId} , F_CHILDNAME = #{childName} , F_TRANSFEE = #{transFee} , F_DELFLAG = #{delFlag} , F_ORDERLISTTYPE = #{orderListType} ,
F_ORDERLISTID = #{orderListId}

mapper.select:
SELECT t.F_ORDERLISTID as orderListId , t.F_ORDERID as orderId , t.F_SPID as spId , t.F_VENID as venId , t.F_VENNAME as venName , t.F_PRICEID as priceId , t.F_PRODUCTID as productId , t.F_PRODUCTNAME as productName , t.F_PRICE as price , t.F_NUM as num , t.F_COUPON_MONEY as couponMoney , t.F_CUSTOMERCOUPONID as customerCouponId , t.F_MONEY as money , t.F_PAYMONEY as payMoney , t.F_BACKNUM as backNum , t.F_GOODSPRICEID as goodsPriceId , t.F_CHILDNAME as childName , t.F_TRANSFEE as transFee , t.F_DELFLAG as delFlag , t.F_ORDERLISTTYPE as orderListType , gs.F_THUMBPIC as thumbPic FROM t_sm_orderlist t LEFT JOIN t_sm_goodsprice gs on t.F_GOODSPRICEID = gs.F_GOODSPRICEID
AND t.F_ORDERID = #{orderId}
AND t.F_DELFLAG = #{delFlag}
AND t.F_ORDERLISTTYPE = #{orderListType}
ORDER BY ${entityPage.sortField}
${entityPage.sortOrder}

SELECT IFNULL(sum(t.F_NUM),0) as num , IFNULL(sum(t.F_MONEY),0) as money , IFNULL(sum(t.F_PAYMONEY),0) as payMoney , IFNULL(sum(t.F_BACKNUM),0) as backNum , IFNULL(sum(t.F_TRANSFEE),0) as transFee FROM t_sm_orderlist t LEFT JOIN t_sm_order o on t.F_ORDERID = o.F_ORDERID
AND t.F_GOODSPRICEID = #{goodsPriceId}
AND (t.F_DELFLAG = #{delFlag} AND o.F_DELFLAG = #{delFlag})
AND t.F_ORDERLISTTYPE = #{orderListType}
AND o.F_PAYTAG = #{payTag}
AND t.F_PRICEID = #{priceId}

SELECT SUM(F_MONEY) AS money, SUM(F_BACKMONEY) AS backMoney, SUM(F_MONEY) - SUM(F_BACKMONEY) AS surplusMoney FROM ( SELECT IFNULL(sum(t.F_MONEY), 0) AS F_MONEY, 0 as F_BACKMONEY FROM t_sm_orderlist t LEFT JOIN t_sm_order o ON t.F_ORDERID = o.F_ORDERID WHERE t.F_ORDERLISTTYPE = '0' AND o.F_PAYTAG = '1'
AND t.F_VENID = #{venId}
AND o.F_PAYDATE >= concat(#{payDateTimeStart},' 00:00:00')
AND o.F_PAYDATE <= concat(#{payDateTimeEnd},' 23:59:59')
UNION ALL SELECT 0 as F_MONEY, IFNULL(sum(t.F_MONEY), 0) AS F_BACKMONEY FROM t_sm_orderlist t LEFT JOIN t_sm_order o ON t.F_ORDERID = o.F_ORDERID WHERE ( t.F_ORDERLISTTYPE = '1' OR t.F_ORDERLISTTYPE = '2' ) AND o.F_PAYTAG = '1'
AND t.F_VENID = #{venId}
AND o.F_PAYDATE >= concat(#{payDateTimeStart},' 00:00:00')
AND o.F_PAYDATE <= concat(#{payDateTimeEnd},' 23:59:59')
)a

select * from ( SELECT ol.F_ORDERLISTTYPE as orderListType, (CASE ol.F_ORDERLISTTYPE WHEN '0' THEN o.F_PAYDATE WHEN '1' THEN ob.F_APPLYTIME WHEN '2' THEN otmp.F_CREATETIME END ) AS createTime, (CASE ol.F_ORDERLISTTYPE WHEN '0' THEN o.F_ORDERCODE WHEN '1' THEN ob.F_BACKCODE WHEN '2' THEN o.F_ORDERCODE END ) AS orderCode, ol.F_VENID as venId, ol.F_MONEY as money, (CASE ol.F_ORDERLISTTYPE WHEN '0' THEN o.F_ORDERID WHEN '1' THEN ob.F_BACKID WHEN '2' THEN o.F_ORDERID END ) AS orderListId FROM t_sm_orderlist ol LEFT JOIN t_sm_order o ON ol.F_ORDERID = o.F_ORDERID AND ol.F_ORDERLISTTYPE = '0' LEFT JOIN t_sm_order otmp ON ol.F_ORDERID = otmp.F_ORDERID AND ol.F_ORDERLISTTYPE = '2' LEFT JOIN t_sm_orderback ob ON ol.F_ORDERID = ob.F_ORDERID AND ol.F_ORDERLISTTYPE = '1' LEFT JOIN t_sm_order od ON ol.F_ORDERID = od.F_ORDERID
AND ol.F_VENID = #{venId}
AND ol.F_DELFLAG = #{delFlag}
AND od.F_PAYTAG = #{payTag}
)olTemp
AND createTime >= concat(#{payDateTimeStart},' 00:00:00')
AND createTime <= concat(#{payDateTimeEnd},' 23:59:59')
AND orderListType = #{orderListType}
ORDER BY createTime DESC

select * from ( SELECT ol.F_ORDERLISTTYPE as orderListType, (CASE ol.F_ORDERLISTTYPE WHEN '0' THEN o.F_PAYDATE WHEN '1' THEN ob.F_APPLYTIME WHEN '2' THEN otmp.F_CREATETIME END ) AS createTime, (CASE ol.F_ORDERLISTTYPE WHEN '0' THEN o.F_ORDERCODE WHEN '1' THEN ob.F_BACKCODE WHEN '2' THEN o.F_ORDERCODE END ) AS orderCode, ol.F_VENID as venId, ol.F_MONEY as money, (CASE ol.F_ORDERLISTTYPE WHEN '0' THEN o.F_ORDERID WHEN '1' THEN ob.F_BACKID WHEN '2' THEN o.F_ORDERID END ) AS orderListId, ol.F_PRODUCTNAME as productName, /*名称*/ ol.F_CHILDNAME as childName, /*规格*/ (CASE ol.F_ORDERLISTTYPE WHEN '0' THEN oc.F_CUSTNAME WHEN '1' THEN '' WHEN '2' THEN oc.F_CUSTNAME END ) AS custName, (CASE ol.F_ORDERLISTTYPE WHEN '0' THEN oc.F_RECADDRESS WHEN '1' THEN '' WHEN '2' THEN oc.F_RECADDRESS END ) AS recAddress, (CASE ol.F_ORDERLISTTYPE WHEN '0' THEN oc.F_CUSTPHONE WHEN '1' THEN '' WHEN '2' THEN oc.F_CUSTPHONE END ) AS custPhone FROM t_sm_orderlist ol LEFT JOIN t_sm_order o ON ol.F_ORDERID = o.F_ORDERID AND ol.F_ORDERLISTTYPE = '0' LEFT JOIN t_sm_order otmp ON ol.F_ORDERID = otmp.F_ORDERID AND ol.F_ORDERLISTTYPE = '2' LEFT JOIN t_sm_orderback ob ON ol.F_ORDERID = ob.F_ORDERID AND ol.F_ORDERLISTTYPE = '1' LEFT JOIN t_sm_order od ON ol.F_ORDERID = od.F_ORDERID LEFT JOIN t_sm_ordercustomer oc ON ol.F_ORDERID = oc.F_ORDERID
AND ol.F_VENID = #{venId}
AND ol.F_DELFLAG = #{delFlag}
AND od.F_PAYTAG = #{payTag}
)olTemp
AND createTime >= concat(#{payDateTimeStart},' 00:00:00')
AND createTime <= concat(#{payDateTimeEnd},' 23:59:59')
AND orderListType = #{orderListType}
ORDER BY createTime DESC

select * from ( SELECT ol.F_ORDERLISTTYPE as orderListType, (CASE ol.F_ORDERLISTTYPE WHEN '0' THEN o.F_PAYDATE WHEN '1' THEN ob.F_APPLYTIME WHEN '2' THEN otmp.F_CREATETIME END ) AS createTime,/*支付时间*/ (CASE ol.F_ORDERLISTTYPE WHEN '0' THEN o.F_ORDERCODE WHEN '1' THEN ob.F_BACKCODE WHEN '2' THEN o.F_ORDERCODE END ) AS orderCode,/*订单编号*/ ol.F_PRODUCTNAME as productName, /*名称*/ ol.F_CHILDNAME as childName, /*规格*/ (CASE ol.F_ORDERLISTTYPE WHEN '0' THEN ol.F_NUM WHEN '1' THEN '' WHEN '2' THEN ol.F_NUM END ) AS num, /*数量*/ (CASE ol.F_ORDERLISTTYPE WHEN '0' THEN ol.F_PRICE WHEN '1' THEN '' WHEN '2' THEN ol.F_PRICE END ) AS price,/*单价*/ (CASE ol.F_ORDERLISTTYPE WHEN '0' THEN o.F_MONEY WHEN '1' THEN ob.F_BACKMONEY WHEN '2' THEN o.F_MONEY END ) AS money,/*总价*/ u.F_USER_NAME as userName,/*订货人*/ oc.F_CUSTNAME as custName, /*收货人*/ oc.F_RECADDRESS as recAddress, oe.F_EPMEORDCODE AS epmeOrdCode, map.F_MAPVALUE AS epmeCompany, (CASE ol.F_ORDERLISTTYPE WHEN '0' THEN oc.F_CUSTPHONE WHEN '1' THEN '' WHEN '2' THEN oc.F_CUSTPHONE END ) AS custPhone FROM t_sm_orderlist ol LEFT JOIN t_sm_order o ON ol.F_ORDERID = o.F_ORDERID AND ol.F_ORDERLISTTYPE = '0' LEFT JOIN t_sm_order otmp ON ol.F_ORDERID = otmp.F_ORDERID AND ol.F_ORDERLISTTYPE = '2' LEFT JOIN t_sm_orderback ob ON ol.F_ORDERID = ob.F_ORDERID AND ol.F_ORDERLISTTYPE = '1' LEFT JOIN t_sm_order od ON ol.F_ORDERID = od.F_ORDERID LEFT JOIN t_pub_user u on o.F_USERID = u.F_USER_ID LEFT JOIN t_sm_ordercustomer oc ON ol.F_ORDERID = oc.F_ORDERID LEFT JOIN t_sm_orderexpress oe on oe.F_ORDERLISTID = ol.F_ORDERLISTID LEFT JOIN t_pub_map map on oe.F_EPMECOMPANY = map.F_MAPKEY and map.F_MAPTYPE='EXPRESS'
AND ol.F_VENID = #{venId}
AND ol.F_DELFLAG = #{delFlag}
AND od.F_PAYTAG = #{payTag}
)olTemp
AND createTime >= concat(#{payDateTimeStart},' 00:00:00')
AND createTime <= concat(#{payDateTimeEnd},' 23:59:59')
AND orderListType = #{orderListType}
ORDER BY createTime DESC
