mapper.select:
SELECT t.F_REFID AS refId, t.F_PRICEID AS priceId, t.F_USERID AS userId, t.F_ORDERLISTID AS orderListId, t.F_REF_PATCHCODE AS refPatchCode, t.F_ISBEGIN AS isBegin, t.F_PRICESTARTDATE AS priceStartDate, t.F_PRICEENDDATE AS priceEndDate, t.F_CREATETIME AS createTime, u.F_USER_NAME as userName, u.F_HEADPIC as headPic FROM t_sm_price_1_ref t left join t_pub_user u on t.F_USERID = u.F_USER_ID
and t.F_REFID = #{refId}
and t.F_PRICEID = #{priceId}
and t.F_ISBEGIN = #{isBegin}
and t.F_REF_PATCHCODE = #{refPatchCode}
ORDER BY ${entityPage.sortField}
${entityPage.sortOrder}

SELECT t.F_REFID AS refId, t.F_PRICEID AS priceId, t.F_USERID AS userId, t.F_ORDERLISTID AS orderListId, t.F_REF_PATCHCODE AS refPatchCode, t.F_ISBEGIN AS isBegin, t.F_PRICESTARTDATE AS priceStartDate, t.F_PRICEENDDATE AS priceEndDate, t.F_CREATETIME AS createTime, u.F_USER_NAME as userName, u.F_HEADPIC as headPic, temp.F_ORDERCOUNT as orderCount, sellPrice.F_PEOPLENUM - temp.F_ORDERCOUNT as num FROM t_sm_price_1_ref t left join t_pub_user u on t.F_USERID = u.F_USER_ID left JOIN (SELECT sum(1) as F_ORDERCOUNT, F_REF_PATCHCODE AS F_REF_PATCHCODE FROM t_sm_price_1_ref GROUP BY F_REF_PATCHCODE) temp on t.F_REF_PATCHCODE = temp.F_REF_PATCHCODE LEFT JOIN (SELECT IFNULL(F_PEOPLENUM,1) as F_PEOPLENUM,F_PRICEID from t_sm_sellprice where F_PRICEID=#{priceId}) sellPrice on t.F_PRICEID = sellPrice.F_PRICEID
t.F_ISBEGIN = 1 and t.F_PRICEID =#{priceId} and (t.F_PRICESTARTDATE <= #{validateDate} and t.F_PRICEENDDATE >= #{validateDate}) and temp.F_ORDERCOUNT < sellPrice.F_PEOPLENUM
ORDER BY ${entityPage.sortField}
${entityPage.sortOrder}
