mapper.select:
SELECT t.F_USEROLEDATAID as userRoleDataId , t.F_ROLETYPE as roleType , t.F_ACCID as accId , t.F_UNITID as unitId , t.F_USERID as userId , t.F_ISDEFAULT as isDefault , u.F_USER_LOGINNAME as userLoginName , u.F_TYPE as userType , u.F_USER_STATE as userState , u.F_USER_LOCKED as userLocked , u.F_USER_PWD as userPwd, t.F_NOLOGINID as noLoginId , t.F_VALIDTIME as validTime FROM t_pub_roledata t LEFT JOIN t_pub_user u on t.F_USERID = u.F_USER_ID
and u.F_USER_LOGINNAME = #{userLoginName} AND LENGTH(u.F_USER_LOGINNAME) = LENGTH(#{userLoginName})
and u.F_TYPE = #{userType}
and t.F_USERID = #{userId}
and t.F_ROLETYPE = #{roleType}
and t.F_ACCID = #{accId}
and t.F_UNITID = #{unitId}
ORDER BY ${entityPage.sortField}
${entityPage.sortOrder}
