mapper.select:
SELECT t.F_SALETYPEID as saleTypeId , t.F_SPID as spId , t.F_SALETYPENAME as saleTypeName , t.F_STATUS as status , t.F_SALETYPEKEY as saleTypeKey , t.F_SALETYPEDESC as saleTypeDesc, t.F_ISSYS as isSys FROM t_sm_saletype t
t.F_SALETYPEID = #{saleTypeId}
and t.F_SALETYPEKEY = #{saleTypeKey}
and t.F_STATUS = #{status}
and t.F_SALETYPENAME like concat('%',#{saleTypeName},'%')
and t.F_SPID like concat('%',#{spId},'%')
ORDER BY ${entityPage.sortField}
${entityPage.sortOrder}

SELECT
	t.F_SALETYPEID AS saleTypeId,
	t.F_SALETYPENAME AS saleTypeName,
	t.F_SALETYPEKEY AS saleTypeKey
FROM
	t_sm_saletype t


    	t.F_SPID = #{spId}


    	and t.F_STATUS = #{status}


UNION ALL 
	SELECT
		c.F_CHANNELID AS saleTypeId,
		c.F_CHANNELNAME AS saleTypeName,
		c.F_CHANNELKEY AS saleTypeKey
	FROM
		t_sm_channel c


    	c.F_SPID = #{spId}


    	and c.F_STATUS = #{status}




    	ORDER BY ${entityPage.sortField}




    	${entityPage.sortOrder}

SELECT
	count(1)
FROM
t_sm_saletype t


    	t.F_SALETYPEID <> #{saleTypeId}


    	and t.F_SALETYPEKEY = #{saleTypeKey}


    	and t.F_SPID = #{spId}
