mapper.update:
UPDATE
	t_sm_shopcart 
SET
	F_PRICEID = #{priceId} ,
	F_NUM = #{num} ,
	F_UPDATETIME = #{updateTime} 


    	and F_USERID = #{userId}


    	and F_PRICEID = #{priceId}


    	and F_CARTID = #{cartId}

mapper.select:
SELECT t.F_CARTID as cartId , t.F_PRICEID as priceId , t.F_NUM as num , t.F_USERID as userId , t.F_CREATETIME as createTime , t.F_UPDATETIME as updateTime FROM t_sm_shopcart t
and t.F_USERID = #{userId}
and t.F_PRICEID = #{priceId}
and t.F_CARTID = #{cartId}

SELECT t.F_CARTID as cartId , t.F_PRICEID as priceId , t.F_NUM as num , t.F_USERID as userId , t.F_CREATETIME as createTime , t.F_UPDATETIME as updateTime , c.F_PRODUCTNAME as productName, g.F_PRICENAME as priceName, s.F_PRICE as price, c.F_THUMBPIC as thumbPic, s.F_THUMBPIC as goodsThumbPic FROM t_sm_shopcart t left join t_sm_sellprice s on t.F_PRICEID = s.F_priceid left join t_sm_commproduct c on s.F_PRODUCTID = c.F_PRODUCTID left join t_sm_goodsprice g on s.F_GOODSPRICEID = g.F_GOODSPRICEID
and t.F_CARTID = #{cartId}
and t.F_USERID = #{userId}
and s.F_DELFLAG=#{delFlag} and c.F_DELFLAG=#{delFlag} and g.F_DELFLAG=#{delFlag}
and c.F_STATE=#{state}
ORDER BY ${entityPage.sortField}
${entityPage.sortOrder}
