mapper.select:
SELECT t.F_CHANNELID as channelId , t.F_SPID as spId , t.F_CHANNELNAME as channelName , t.F_STATUS as status , t.F_CHANNELKEY as channelKey , t.F_CHANNELDESC as channelDesc , t.F_ISSYS as isSys FROM t_sm_channel t
t.F_CHANNELID = #{channelId}
and t.F_CHANNELKEY = #{channelKey}

SELECT t.F_CHANNELID as channelId , t.F_SPID as spId , t.F_CHANNELNAME as channelName , t.F_STATUS as status , t.F_CHANNELKEY as channelKey , t.F_CHANNELDESC as channelDesc , t.F_ISSYS as isSys FROM t_sm_channel t
t.F_CHANNELID = #{channelId}
and t.F_SPID = #{spId}
and t.F_CHANNELNAME like concat('%',#{channelName},'%')
ORDER BY ${entityPage.sortField}
${entityPage.sortOrder}

SELECT
	count(1)
FROM
t_sm_channel t


    	t.F_CHANNELID <> #{channelId}


    	and t.F_SPID = #{spId}


    	and t.F_CHANNELKEY = #{channelKey}
